import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../../../shared/products.service'; // Adjust the path as needed

@Component({
  selector: 'app-form-products',
  templateUrl: './form-products.component.html',
  styleUrls: ['./form-products.component.css']
})
export class FormProductsComponent {
  productForm: FormGroup;
  selectedImage: File | null = null; // For storing the selected image file

  constructor(private formBuilder: FormBuilder, private productService: ProductService) {
    this.productForm = this.formBuilder.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      price: [0, [Validators.required, Validators.min(0)]],
      categoria: ['', Validators.required],
      onSale: [false]
    });
  }

  onSubmit() {
    if (this.productForm.valid) {
      const formData = new FormData();
      formData.append('name', this.productForm.get('name')?.value);
      formData.append('description', this.productForm.get('description')?.value);
      formData.append('price', this.productForm.get('price')?.value);
      formData.append('categoria', this.productForm.get('categoria')?.value);
      formData.append('onSale', this.productForm.get('onSale')?.value);

      if (this.selectedImage) {
        formData.append('image', this.selectedImage);
      }

      this.productService.submitProduct(formData).subscribe(
        response => {
          console.log('Form submitted successfully!', response);
        },
        error => {
          console.error('Error submitting form!', error);
        }
      );
    } else {
      console.log('Form is invalid!');
    }
  }

  onImageSelected(event: any) {
    if (event.target.files && event.target.files.length > 0) {
      this.selectedImage = event.target.files[0];
    }
  }
}
