import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
    selector: 'app-editar-usuario',
    templateUrl: './form-products.component.html',
    styleUrls: ['./form-products.component.css']
})

    export class FormProductsComponent {
      productForm: FormGroup;
      nameValue: string = ''; // Initialize with an empty string
      descriptionValue: string = ''; // Initialize with an empty string
      priceValue: number = 0; // Initialize with a default value
      categoriaValue: string = ''; // Initialize with an empty string
      onSaleValue: boolean = false; // Initialize with a default value
    
      constructor(private formBuilder: FormBuilder) {
        this.productForm = this.formBuilder.group({
          name: ['', Validators.required],
          description: ['', Validators.required],
          price: [0, [Validators.required, Validators.min(0)]],
          categoria: ['', Validators.required],
          onSale: [false]
          // other form controls
        });
      }

    onSubmit() {
        // Implement your form submission logic here
    }

    onImageSelected(event: any) {
        // Implement your image selection logic here
    }
}
