import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../../../shared/products.service'; // Adjust the path as needed
import { ProductModel } from 'src/app/shared/products.model';

@Component({
  selector: 'app-form-products',
  templateUrl: './form-products.component.html',
  styleUrls: ['./form-products.component.css']
})
export class FormProductsComponent {
  productForm: FormGroup;
  selectedImage: File | null = null;


  constructor(private formBuilder: FormBuilder, private productService: ProductService) {
    this.productForm = this.formBuilder.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      price: [0, [Validators.required, Validators.min(0)]],
      categoria: ['', Validators.required],
      onSale: [false],
      image: ['', Validators.required] // Add image to form control for validation

    });
  }

  onSubmit() {
    if (this.productForm.valid) {
      const product: ProductModel = {
        name: this.productForm.get('name')?.value,
        description: this.productForm.get('description')?.value,
        price: this.productForm.get('price')?.value,
        categoria: this.productForm.get('categoria')?.value,
        onSale: this.productForm.get('onSale')?.value,
        image: `assets/images/${this.selectedImage?.name}`,  // Ruta relativa a la carpeta assets
        
        id: ''
      };
  
      this.productService.addProduct(product).subscribe(
        response => {
          console.log('Form submitted successfully!', response);
        },
        error => {
          console.error('Error submitting form!', error);
        }
      );
    } else {
      console.log('Form is invalid!');
    }
  }

  onImageSelected(event: any) {
    if (event.target.files && event.target.files.length > 0) {
      this.selectedImage = event.target.files[0];
    }
  }
}
<div class="flex flex-col items-center mb-96 mt-32">
  <form class="bg-gray-100 p-8 rounded-lg shadow-md w-full max-w-md" [formGroup]="productForm" (ngSubmit)="onSubmit()">
    <div class="flex flex-col mb-5">
      <label for="name">Nombre (máx: 20)*</label>
      <input type="text" name="name" id="name" formControlName="name" class="border border-gray-300 rounded p-2" required>
      <div *ngIf="productForm.get('name')?.errors?.['required']" class="text-red-500 text-xs mt-1">Nombre requerido</div>
      <div *ngIf="productForm.get('name')?.errors?.['maxlength']" class="text-red-500 text-xs mt-1">Nombre demasiado largo</div>
    </div>
    
    <div class="flex flex-col mb-5">
      <label for="description">Descripción (máx: 30)*</label>
      <textarea name="description" id="description" rows="3" formControlName="description" class="border border-gray-300 rounded p-2" required></textarea>
      <div *ngIf="productForm.get('description')?.errors?.['required']" class="text-red-500 text-xs mt-1">Descripción requerida</div>
      <div *ngIf="productForm.get('description')?.errors?.['maxlength']" class="text-red-500 text-xs mt-1">Descripción demasiado larga</div>
    </div>
    
    <div class="flex flex-col mb-5">
      <label for="price">Precio*</label>
      <input type="number" name="price" id="price" formControlName="price" class="border border-gray-300 rounded p-2" required>
      <div *ngIf="productForm.get('price')?.errors?.['required']" class="text-red-500 text-xs mt-1">Precio requerido</div>
      <div *ngIf="productForm.get('price')?.errors?.['min']" class="text-red-500 text-xs mt-1">Precio inválido</div>
      <div *ngIf="productForm.get('price')?.errors?.['max']" class="text-red-500 text-xs mt-1">Precio demasiado alto</div>
    </div>
    
    <div class="flex flex-col mb-5">
      <label for="image">Imagen*</label>
      <input type="file" (change)="onImageSelected($event)" accept="image/*">
      <img class="w-full h-80 object-cover rounded-t-lg" [src]="product.image" alt="product image">

    </div>
    
    <div class="flex flex-col mb-5">
      <label for="categoria">Categorías*</label>
      <select name="categoria" id="categoria" formControlName="categoria" class="border border-gray-300 rounded p-2" required>
        <option value="" disabled selected>Seleccionar categoría</option>
        <option value="Equipacion L">Equipación Local</option>
        <option value="Equipacion V">Equipación Visitante</option>
        <option value="Equipacion E1">Equipación Entreno 1</option>
        <option value="Equipacion E2">Equipación Entreno 2</option>
        <option value="Pantalon F">Pantalon F</option>
        <option value="Pantalon M">Pantalon M</option>
        <option value="Chaqueta">Chaqueta</option>
        <option value="Mochila">Mochila</option>
      </select>
      <div *ngIf="productForm.get('categoria')?.errors?.['required']" class="text-red-500 text-xs mt-1">Categoría requerida</div>
    </div>
    
    <div class="flex flex-col mb-5">
      <label for="onSale" class="text-lg text-gray-700">En venta</label>
      <input type="checkbox" name="onSale" id="onSale" formControlName="onSale" class="h-6 w-6 text-indigo-600 border-gray-300 rounded focus:ring-indigo-500">
    </div>
    
    <button type="submit" [disabled]="productForm.invalid" class="disabled:opacity-50 disabled:cursor-not-allowed bg-blue-500 text-white font-medium py-2 px-4 rounded mt-4 w-full">
      Enviar
    </button>
  </form>
</div>
