import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ProductsModel } from './products.model';

@Injectable({
  providedIn: 'root'
})
export class Productservice {

  BASE_URL = "http://localhost:3000"

  constructor(private http: HttpClient) { }

  obtenerproducts() {
    return this.http.get<ProductsModel[]>(this.BASE_URL+'/products');
  }

  obtenerUsuario(id: string) {
    return this.http.get<ProductsModel[]>(`${this.BASE_URL}/products/${id}`);
  }

  agregarUsuario(product: ProductsModel) {
    return this.http.post<string>(`${this.BASE_URL}/products/agregar`, product);
  }

  actualizarUsuario(product: ProductsModel) {
    return this.http.put<string>(`${this.BASE_URL}/products/actualizar/${product}`, product)
  }

  borrarUsuario(id: string) {
    return this.http.delete<string>(`${this.BASE_URL}/products/borrar/${id}`)
  }
}
