import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../../../shared/products.service';

@Component({
  selector: 'app-form-products',
  templateUrl: './form-products.component.html',
  styleUrls: ['./form-products.component.css']
})
export class FormProductsComponent {
  productForm: FormGroup;
  selectedFile: File | null = null;

  constructor(private formBuilder: FormBuilder, private productService: ProductService) {
    this.productForm = this.formBuilder.group({
      name: ['', [Validators.required, Validators.maxLength(20)]],
      description: ['', [Validators.required, Validators.maxLength(30)]],
      price: [0, [Validators.required, Validators.min(0), Validators.max(10000)]],
      categoria: ['', Validators.required],
      onSale: [false],
      image: [null, Validators.required],
    });
  }

  onImageSelected(event: any) {
    this.selectedFile = event.target.files[0];
  }

  onSubmit() {
    if (this.productForm.valid && this.selectedFile) {
      const formData = new FormData();
      formData.append('name', this.productForm.get('name')!.value);
      formData.append('description', this.productForm.get('description')!.value);
      formData.append('price', this.productForm.get('price')!.value);
      formData.append('categoria', this.productForm.get('categoria')!.value);
      formData.append('onSale', this.productForm.get('onSale')!.value);
      formData.append('image', this.selectedFile);

      this.productService.addProduct(formData).subscribe(
        response => {
          console.log('Product added successfully', response);
          this.productForm.reset();
        },
        error => {
          console.error('Error adding product', error);
        }
      );
    } else {
      console.error('Invalid form');
    }
  }
}
<div class="flex flex-col items-center mb-96 mt-32">
  <form class="bg-gray-100 p-8 rounded-lg shadow-md w-full max-w-md" [formGroup]="productForm" (ngSubmit)="onSubmit()">
    <div class="flex flex-col mb-5">
      <label for="name">Nombre (máx: 20)*</label>
      <input type="text" name="name" id="name" formControlName="name" [(ngModel)]="nameValue" class="border border-gray-300 rounded p-2" required>
      <div *ngIf="productForm.get('name')?.invalid && (productForm.get('name')?.dirty || productForm.get('name')?.touched)" class="text-red-500 text-xs mt-1">
        <div *ngIf="productForm.get('name')?.errors?.required">Nombre requerido</div>
        <div *ngIf="productForm.get('name')?.value?.length > 20">Nombre demasiado largo</div>
      </div>
    </div>

    <div class="flex flex-col mb-5">
      <label for="description">Descripción (máx: 30)*</label>
      <textarea name="description" id="description" rows="3" formControlName="description" [(ngModel)]="descriptionValue" class="border border-gray-300 rounded p-2"></textarea>
      <div *ngIf="productForm.get('description')?.invalid && (productForm.get('description')?.dirty || productForm.get('description')?.touched)" class="text-red-500 text-xs mt-1">
        <div *ngIf="productForm.get('description')?.errors?.required">Descripción requerida</div>
        <div *ngIf="productForm.get('description')?.value?.length > 30">Descripción demasiado larga</div>
      </div>
    </div>

    <div class="flex flex-col mb-5">
      <label for="price">Precio*</label>
      <input type="number" name="price" id="price" formControlName="price" [(ngModel)]="priceValue" class="border border-gray-300 rounded p-2" required>
      <div *ngIf="productForm.get('price')?.invalid && (productForm.get('price')?.dirty || productForm.get('price')?.touched)" class="text-red-500 text-xs mt-1">
        <div *ngIf="productForm.get('price')?.errors?.required">Precio requerido</div>
        <div *ngIf="productForm.get('price')?.value <= 0">Precio inválido</div>
        <div *ngIf="productForm.get('price')?.value > 10000">Precio demasiado alto</div>
      </div>
    </div>

    <div class="flex flex-col mb-5">
      <label for="image">Imagen*</label>
      <input type="file" name="image" id="image" formControlName="image" (change)="onImageSelected($event)" class="border border-gray-300 rounded p-2" required>
    </div>

    <div class="flex flex-col mb-5">
      <label for="categoria">Categorías*</label>
      <select name="categoria" id="categoria" formControlName="categoria" [(ngModel)]="categoriaValue" class="border border-gray-300 rounded p-2">
        <option value="" disabled selected>Seleccionar categoría</option>
        <option value="Equipacion L">Equipación Local</option>
        <option value="Equipacion V">Equipación Visitante</option>
        <option value="Equipacion E1">Equipación Entreno 1</option>
        <option value="Equipacion E2">Equipación Entreno 2</option>
        <option value="Pantalon F">Pantalon F</option>
        <option value="Pantalon M">Pantalon M</option>
        <option value="Chaqueta">Chaqueta</option>
        <option value="Mochila">Mochila</option>
      </select>
      <div *ngIf="productForm.get('categoria')?.invalid && (productForm.get('categoria')?.dirty || productForm.get('categoria')?.touched)" class="text-red-500 text-xs mt-1">
        <div *ngIf="productForm.get('categoria')?.errors?.required">Categoría requerida</div>
      </div>
    </div>

    <div class="flex flex-col mb-5">
      <label for="onSale" class="text-lg text-gray-700">En venta</label>
      <input type="checkbox" name="onSale" id="onSale" formControlName="onSale" [(ngModel)]="onSaleValue" class="h-6 w-6 text-indigo-600 border-gray-300 rounded focus:ring-indigo-500">
    </div>

    <button type="submit" [disabled]="productForm.invalid" class="disabled:opacity-50 disabled:cursor-not-allowed bg-blue-500 text-white font-medium py-2 px-4 rounded mt-4 w-full">
      Enviar
    </button>
  </form>
</div>
